import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';

// Remove the default icon warning
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const Game = () => {
  const [currentLocation, setCurrentLocation] = useState(null);
  const [userPosition, setUserPosition] = useState(null);
  const [distance, setDistance] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);

  const fetchNewLocation = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/get-location');
      setCurrentLocation(response.data);
      setGameStarted(true);
      setUserPosition(null);
      setDistance(null);
    } catch (error) {
      console.error('Error fetching location:', error);
    }
  };

  const handleMapClick = (e) => {
    setUserPosition(e.latlng);

    if (currentLocation) {
      axios.post('http://localhost:5000/api/check-distance', {
        target_lat: currentLocation.lat,
        target_lng: currentLocation.lng,
        user_lat: e.latlng.lat,
        user_lng: e.latlng.lng
      })
      .then(response => {
        setDistance(response.data.distance);
      })
      .catch(error => console.error('Error checking distance:', error));
    }
  };

  return (
    <div className="game-container">
      <div className="game-header">
        <h1>Boston Location Game</h1>
        <button onClick={fetchNewLocation} disabled={!gameStarted}>
          {gameStarted ? 'New Location' : 'Start Game'}
        </button>
      </div>

      {currentLocation && (
        <div className="location-info">
          <h2>Find this location:</h2>
          <img 
            src={currentLocation.image} 
            alt={currentLocation.name}
            style={{ maxWidth: '300px', marginBottom: '20px' }}
          />
          <p>Click on the map to guess the location</p>
        </div>
      )}

      <MapContainer 
        center={[42.3601, -71.0589]} 
        zoom={13}
        style={{ height: '600px', width: '100%' }}
        onClick={handleMapClick}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {currentLocation && (
          <Marker position={[currentLocation.lat, currentLocation.lng]}>
            <Popup>
              {currentLocation.name}
            </Popup>
          </Marker>
        )}

        {userPosition && (
          <Marker position={userPosition}>
            <Popup>
              Your guess
              {distance !== null && <p>Distance: {distance} km</p>}
            </Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
};

export default Game;
